pragma solidity ^0.5.0;
import "./Items.sol";

contract Platform is Items{

    function newItem(string memory name, string memory description) public payable returns(uint) {
        return _createItem(name, description);
    }

    function changeItem(uint id1, uint id2) public payable{
        _changeWith(id1, id2);
    }

    // function addPreferAndCheck(uint id, uint preferId) public view returns(bool) {
    //     return _addPreferAndCheck(id, preferId);
    // }

    function listUserItem() public view returns (uint[] memory) {
        return _listItems(msg.sender);
    }

    function getSize() public view returns (uint)  {
        return _listSize();
    }

    function getItem(uint i) public view returns (string memory, string memory) {
        return _item(i, msg.sender);
    }

    function listMatchItems() public view returns (uint[] memory) {
        return _getMatchItems(msg.sender);
    }
}
